cy.request("POST", `${apiUrl}/auth/register`, {
  username: employeeUsername,
  password: employeePassword
}).then(response => {
  expect(response.status).to.eq(201);
  expect(response.body).to.have.property("message", "User registered successfully");
});

cy.request("POST", `${apiUrl}/auth/login`, {
  username: employeeUsername,
  password: employeePassword
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body).to.have.property("token");
  const token = response.body.token;
  Cypress.env("employeeAuthToken", token);
  // Decode JWT payload to extract employeeId
  const payloadBase64 = token.split(".")[1];
  const payloadString = atob(payloadBase64);
  const payload = JSON.parse(payloadString);
  // payload.id is the in-memory user ID
  Cypress.env("employeeId", payload.id);
});

const token = Cypress.env("employeeAuthToken");
const employeeId = Cypress.env("employeeId");
cy.request({
  method: "POST",
  url: `${apiUrl}/user/resign`,
  headers: {
    Authorization: `${token}`,
    "Content-Type": "application/json"
  },
  body: {
    employeeId,
    // newly added
    lwd: "2024-12-26"
  }
}).then(response => {
  expect(response.status).to.eq(200);
  // The API sends back { data: { resignation: { _id: ... } } }
  employeeResignationId = response.body.data.resignation._id;
  expect(employeeResignationId).to.be.a("string");
});

