});

const token = Cypress.env("adminAuthToken");
const expectedResponses = [{
  questionText: "What prompted you to start looking for another job?",
  response: "Lack of career growth opportunities"
}, {
  questionText: "Would you recommend this company to others?",
  response: "Yes, with some reservations"
}];
cy.request({
  method: "GET",
  url: `${apiUrl}/admin/exit_responses`,
  headers: {
    Authorization: `${token}`
  }
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body.data).to.be.an("array");
  // Find an entry whose responses match exactly our expectedResponses
  const hasExpected = response.body.data.some(item => {
    if (item.resignationId !== employeeResignationId) {
      return false;
    }
    // Check that the array of responses matches
    return Array.isArray(item.responses) && item.responses.length === expectedResponses.length && item.responses.every((resp, i) => {
      return resp.questionText === expectedResponses[i].questionText && resp.response === expectedResponses[i].response;
    });
  });
  expect(hasExpected).to.be.true;
});
