const token = Cypress.env("employeeAuthToken");
// Your backend now extracts employeeId from the Bearer token, 
// so you only need to send { lwd: <ISO‐date‐string> }.
cy.request({
  method: "POST",
  url: `${apiUrl}/user/resign`,
  headers: {
    Authorization: `Bearer ${token}`
  },
  body: {
    lwd: "2024-12-26" // Must be a valid format your backend accepts
  }
}).then(response => {
  expect(response.status).to.eq(200);
  // Cypress should receive { data: { resignation: { _id: "..." } } }
  expect(response.body).to.have.nested.property("data.resignation._id");
  employeeResignationId = response.body.data.resignation._id;
});

