cy.request({
  method: "POST",
  url: `${apiUrl}/auth/register`,
  body: {
    username: employeeUsername,
    email: employeeEmail,
    password: employeePassword
  },
  failOnStatusCode: false // allow 201 or 409
}).then(response => {
  // We expect either:
  //  • 201 Created  (new user),
  //  • 409 Conflict (username/email already exists)
  expect([201, 409]).to.include(response.status);
  if (response.status === 201) {
    expect(response.body).to.have.property("message", "User registered successfully");
  }
});

cy.request({
  method: "POST",
  url: `${apiUrl}/auth/login`,
  body: {
    username: employeeUsername,
    password: employeePassword
  }
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body).to.have.property("token");
  // Store the raw JWT for later
  Cypress.env("employeeAuthToken", response.body.token);
});

const token = Cypress.env("employeeAuthToken");
cy.request({
  method: "POST",
  url: `${apiUrl}/user/resign`,
  headers: {
    Authorization: `Bearer ${token}`
  },
  body: {
    // Our backend now looks at `intendedLastWorkingDay` (not `lwd`).
    intendedLastWorkingDay: "2024-12-26"
  },
  failOnStatusCode: false
}).then(response => {
  // Expect status 200 & a resignation ID
  expect(response.status).to.eq(200);
  expect(response.body).to.have.nested.property("data.resignation._id");
  employeeResignationId = response.body.data.resignation._id;
});

// The deployed backend expects { username, password } for login.
// We assume the HR/admin user was created with username "admin" and password "admin".
cy.request({
  method: "POST",
  url: `${apiUrl}/auth/login`,
  body: {
    username: "admin",
    password: "admin"
  }
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body).to.have.property("token");
  Cypress.env("adminAuthToken", response.body.token);
});

const token = Cypress.env("adminAuthToken");
cy.request({
  method: "GET",
  url: `${apiUrl}/admin/resignations`,
  headers: {
    Authorization: `Bearer ${token}`
  },
  failOnStatusCode: false
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body.data).to.be.an("array");
  // Confirm that our earlier resignation _id is present
  const found = response.body.data.some(r => r._id === employeeResignationId);
  expect(found).to.be.true;
});

const token = Cypress.env("adminAuthToken");
cy.request({
  method: "PUT",
  url: `${apiUrl}/admin/conclude_resignation`,
  headers: {
    Authorization: `Bearer ${token}`,
    "Content-Type": "application/json"
  },
  body: {
    resignationId: employeeResignationId,
    approved: true,
    // Again, use `intendedLastWorkingDay` rather than `lwd`
    intendedLastWorkingDay: "26 Dec 2024"
  },
  failOnStatusCode: false
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body.data).to.have.property("status", "Approved");
});

const token = Cypress.env("employeeAuthToken");
cy.request({
  method: "POST",
  url: `${apiUrl}/user/responses`,
  headers: {
    Authorization: `Bearer ${token}`,
    "Content-Type": "application/json"
  },
  body: {
    resignationId: employeeResignationId,
    responses: [{
      questionText: "What prompted you to start looking for another job?",
      response: "Lack of career growth opportunities"
    }, {
      questionText: "Would you recommend this company to others?",
      response: "Yes, with some reservations"
    }]
  },
  failOnStatusCode: false
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body.data).to.have.property("resignationId", employeeResignationId);
});

const token = Cypress.env("adminAuthToken");
cy.request({
  method: "GET",
  url: `${apiUrl}/admin/exit_responses`,
  headers: {
    Authorization: `Bearer ${token}`
  },
  failOnStatusCode: false
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body.data).to.be.an("array");
  // Check that an entry matches our resignationId and expected responses:
  const expected = [{
    questionText: "What prompted you to start looking for another job?",
    response: "Lack of career growth opportunities"
  }, {
    questionText: "Would you recommend this company to others?",
    response: "Yes, with some reservations"
  }];
  const found = response.body.data.some(item => {
    if (item.resignationId !== employeeResignationId) return false;
    return Array.isArray(item.responses) && item.responses.length === expected.length && item.responses.every((resp, i) => {
      return resp.questionText === expected[i].questionText && resp.response === expected[i].response;
    });
  });
  expect(found).to.be.true;
});
