tests.code:
cy.request({
  method: 'POST',
  url: 'https://exit-kxgu.onrender.com/api/auth/register',
  body: {
    username: 'testemployee',
    email: 'testemployee@example.com',
    password: 'password123'
  }
}).then(response => {
  expect(response.status).to.eq(201);
  expect(response.body).to.have.property('message', 'User registered successfully');
});

cy.request({
  method: 'POST',
  url: 'https://exit-kxgu.onrender.com/api/auth/login',
  body: {
    email: 'testemployee@example.com',
    password: 'password123'
  }
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body).to.have.property('token');
  employeeToken = response.body.token;
});

cy.request({
  method: 'POST',
  url: 'https://exit-kxgu.onrender.com/api/user/resign',
  headers: {
    Authorization: `Bearer ${employeeToken}`
  },
  body: {
    lwd: "2024-12-26"
  }
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body.data).to.have.property('resignation');
  expect(response.body.data.resignation).to.have.property('_id');
  resignationId = response.body.data.resignation._id;
});

cy.request({
  method: 'POST',
  url: 'https://exit-kxgu.onrender.com/api/auth/login',
  body: {
    email: 'admin@example.com',
    password: 'admin'
  }
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body).to.have.property('token');
  adminToken = response.body.token;
});

cy.request({
  method: 'GET',
  url: 'https://exit-kxgu.onrender.com/api/admin/resignations',
  headers: {
    Authorization: `Bearer ${adminToken}`
  }
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body.data).to.be.an('array');
  // Verify our test resignation is in the list
  const testResignation = response.body.data.find(r => r._id === resignationId);
  expect(testResignation).to.exist;
});

cy.request({
  method: 'PUT',
  url: 'https://exit-kxgu.onrender.com/api/admin/conclude_resignation',
  headers: {
    Authorization: `Bearer ${adminToken}`
  },
  body: {
    resignationId: resignationId,
    approved: true,
    lwd: "26 Dec 2024"
  }
}).then(response => {
  expect(response.status).to.eq(200);
  expect(response.body.data).to.have.property('status', 'Approved');
  expect(response.body.data).to.have.property('exitDate');
});
